package com.example.t30_advertisementbanner;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentStatePagerAdapter;
import androidx.viewpager.widget.ViewPager;

import android.os.Bundle;
import android.os.Handler;
import android.os.Message;
import android.util.Log;

public class MainActivity extends AppCompatActivity implements ViewPager.OnPageChangeListener {
    ViewPager viewPager;
    CustomAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        viewPager = findViewById(R.id.viewpager);
        adapter = new CustomAdapter(getSupportFragmentManager());
        viewPager.setAdapter(adapter);
        viewPager.addOnPageChangeListener(this);
        handler.sendEmptyMessageAtTime(0,3000);
    }

    String[] urls = {
      "data:image/jpeg;base64,",
      "data:image/jpeg;base64,",
      "data:image/jpeg;base64,",
      "data:image/jpeg;base64,",
      "data:image/jpeg;base64,"
    };

    @Override
    public void onPageScrolled(int position, float positionOffset, int positionOffsetPixels) {

    }

    @Override
    public void onPageSelected(int position) {

    }

    @Override
    public void onPageScrollStateChanged(int state) {
        handler.removeMessages(0);
        if(state == viewPager.SCROLL_STATE_IDLE){
            handler.sendEmptyMessageDelayed(0,3000);
        }
    }

    class CustomAdapter extends FragmentStatePagerAdapter{

        public CustomAdapter(@NonNull FragmentManager fm) {
            super(fm);
        }


        @NonNull
        @Override
        public Fragment getItem(int position) {
            Advertisement ad = null;
            Log.d("mood","posision: "+ position);
            //  position  1 !!
            // 1,0,2...    ... ?
            if(position%5 == 0){
                ad = new Advertisement();
                ad.url = urls[0];
            }else if(position%5 == 1){
                ad = new Advertisement();
                ad.url = urls[1];
            }else if(position%5 == 2){
                ad = new Advertisement();
                ad.url = urls[2];
            }else if(position%5 == 3){
                ad = new Advertisement();
                ad.url = urls[3];
            }else if(position%5 == 4){
                ad = new Advertisement();
                ad.url = urls[4];
            }
            return ad;
        }

        @Override
        public int getCount() {
            return 5;
        }
    }

    Handler handler = new Handler(){
        @Override
        public void handleMessage(@NonNull Message msg) {
            super.handleMessage(msg);
            viewPager.setCurrentItem((viewPager.getCurrentItem()+1)%5,false);
            handler.sendEmptyMessageDelayed(0,3000);

        }
    };
}
